# how may elements are in each of the following lists?
first_list = ['ybk', 'zcg', 'dhw', 'gua', 'mqh', 'yel', 'fpk', 'imk', 'nkt', 'tpl', 'guc', 'ozk', 'dip', 'thn', 'pxc', 'obk', 'orl', 'rdc', 'vto', 'wen', 'tbw', 'itj', 'qfb', 'ymd', 'txb', 'vgw', 'wgd', 'tcg', 'ezm', 'gnt', 'ehf', 'pfo', 'alf']
second_list = ['ybk', 'zcg', 'dhw', 'gua', 'mqh', 'yel', 'fpk', 'imk', 'nkt', 'tpl', 'guc', 'ozk', 'dip', 'thn', 'pxc', 'obk', 'orl', 'rdc', 'vto', 'wen', 'tbw', 'itj', 'qfb', 'ymd', 'txb', 'vgw', 'wgd', 'tcg', 'ezm', 'gnt', 'ehf', 'pfo', 'alf']

print ("Die erste Liste ist",len(first_list), "Elemente lang")
print ("Die zweite Liste ist",len(second_list),"Elemente lang")

# which and how many elements are common to the two lists above?
ListeGleicheElemente = list(set(first_list) & set(second_list))

print("Folgende Elemente sind in beiden Listen identisch:",ListeGleicheElemente)
print("Die beiden Listen haben",len(ListeGleicheElemente),"gleiche Elemente")

# how often does the character 'a' occur in each of the lists above?
aAnzahl1 = str("".join(first_list)).count("a")
aAnzahl2 = str("".join(second_list)).count("a")

print ("In der ersten Liste beträt die Anzahl an \"a\"",aAnzahl1)
print("In der zweiten Liste beträt die Anzahl an \"a\"",aAnzahl2)